mapper.update:
UPDATE CLUBJOINLIST
SET JOIN_STATUS = '승인'
WHERE JOIN_NUM = #{joinNum}

UPDATE CLUBJOINLIST
SET JOIN_STATUS = '거절'
WHERE JOIN_NUM = #{joinNum}

mapper.select:
SELECT club_num, club_name, club_info, club_president, TO_CHAR(club_regdate,'yyyy-MM-DD') club_regdate, club_membercount, club_photo
FROM club
WHERE club_num = #{clubNum}

SELECT CLUB_NAME
FROM CLUB
WHERE CLUB_NAME = #{clubName}
