mapper.select:
select member_name "name", member_storedphoto "storedProfile", club_num "clubNum"
from member
where club_num = #{value}

mapper.delete:
DELETE
FROM MATCHBOARD
WHERE club_num = #{clubNum}
  AND mb_type = #{type}

mapper.select:
SELECT mb_num, mb_name, mb_file, club_num, mb_type
FROM MATCHBOARD
WHERE club_num = #{clubNum}
  AND mb_type = #{type}
